import React from 'react';

const Home = () => {

    const currentTine = new Date();
    currentTine.getHours();
    let dynamicTime = '';

    if(currentTine > 1 && currentTine < 12){
        this.dynamicTime = 'Good Morning'

    }

    return(
        <>
        <div className="welcome">
        <div style={{ marginTop: '-45px'}} className="d-flex text-center flex-column w-100 vh-100 align-items-center justify-content-center">
    <h1>Hi Friends <strong>{dynamicTime}!</strong></h1>
            <p className="font-weight-300" style={{ fontSize: '16px'}}>Welcome to our React CRUD application</p>
        </div>
        </div>
        </>
    )
}

export default Home;
